'  l_metric_tab ASHA_CUBE_PKG.tt_metric_tab;',
'begin',
'  ',
'  select proj_name into :ASH_ANALYZER_CURRENT_PROJ from asha_cube_projects where proj_id=:P3_PROJ_ID;',
'  ',
'  if :P3_ON_CLICK = ''YES'' then',
'    if COREMOD_SEC.is_role_assigned(''ASH_ANALYZER'',''Reas-write users'') then',
'      ASHA_CUBE_PKG.init_session   (  P_PROJ_ID => :P3_PROJ_ID, P_RETENTION => null, P_SESS_ID => :P3_SESS_ID) ;  ',
