'begin',
'  if :P1000_MODE=''EDIT'' then',
'    :P1000_CAPTURE := ''Edit'';',
'  elsif :P1000_MODE=''CREATE'' then',
'    :P1000_CAPTURE := ''Create'';    ',
'  else',
'    :P1000_CAPTURE := ''Wrong mode'';',
'  end if;',
'',
'  select',
'         ot.ot_icon,',
'         ot.ot_name,',
'         to_char(o.obj_created,''&APP_GRID_DT_FMT.''),',
'         to_char(o.obj_expired,''&APP_GRID_DT_FMT.''),',
