'    into ',
'        :P1000_ICON,',
'        :P1000_OT_NAME,',
'        :P1000_CREATED,',
'        :P1000_EXPIRED,',
'        :P1000_NAME,',
'        :P1000_DESCR,',
'        :P1000_SORTORDR,',
'        :P1000_OWNER,',
'        :P1000_IS_PUBLIC,',
'        l_retention,',
'        :P1000_OBJECT_PAGE,',
'        :P1000_IS_READONLY,',
'        :P1000_SIZE',
'    from ',
'         v$opas_objects o, opas_object_types ot ',
'   where o.obj_id = :P1000_OBJ_ID and o.obj_ot = ot.ot_id;',
