,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'begin',
'',
'  if coalesce(:P6006_HISTOGRAM, :P6006_INSTANCES, :P6006_START_DT, :P6006_END_DT,',
'              :P6006_HISTOGRAM_1, :P6006_INSTANCES_1, :P6006_START_DT_1, :P6006_END_DT_1) is null then',
