'    COREMOD_APPSTATE.get_jparam_m(''P6006_START_DT'',:P6006_START_DT);',
'    COREMOD_APPSTATE.get_jparam_m(''P6006_END_DT'',:P6006_END_DT);',
'    COREMOD_APPSTATE.get_jparam_m(''P6006_HISTOGRAM'',:P6006_HISTOGRAM);',
'	COREMOD_APPSTATE.get_jparam_m(''P6006_INSTANCES'',:P6006_INSTANCES);',
'	',
'    COREMOD_APPSTATE.get_jparam_m(''P6006_START_DT_1'',:P6006_START_DT_1);',
'    COREMOD_APPSTATE.get_jparam_m(''P6006_END_DT_1'',:P6006_END_DT_1);',
'    COREMOD_APPSTATE.get_jparam_m(''P6006_HISTOGRAM_1'',:P6006_HISTOGRAM_1);',
'	COREMOD_APPSTATE.get_jparam_m(''P6006_INSTANCES_1'',:P6006_INSTANCES_1);	',
'  end if;',
'',
'  if :P6006_START_DT is null or :P6006_END_DT is null then',
'    select to_char(max(tim_tz) - interval ''5'' minute - interval ''1'' day,''&APP_GRID_DT_FMT_TZ.''), to_char(max(tim_tz) + interval ''5'' minute,''&APP_GRID_DT_FMT_TZ.'')',
'      into :P6006_START_DT, :P6006_END_DT ',
'      from opas_ot_db_mon_hist_sess ',
'     where dbmh_id = :P6006_HISTOGRAM; -- and tim_tz > systimestamp - interval ''1'' day;',
'  end if;',
'  begin',
'    select dbmh_name || '' (''|| h.dblink || '') from ''||:P6006_START_DT||'' to ''||:P6006_END_DT',
'      into :P6006_HIST_CAPTION',
'      from opas_ot_db_mon_hist h ',
'     where dbmh_id = :P6006_HISTOGRAM;',
'  exception',
'    when NO_DATA_FOUND then :P6006_HIST_CAPTION := ''N/A'';',
'  end;',
'  ------------------------------',
'  if :P6006_START_DT_1 is null or :P6006_END_DT_1 is null then',
