,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with including_tags as ',
'  (select tag_name',
'     from OPAS_OT_SQL_TAGS',
'    start with tag_name in (select column_value from table(coreobj_sql_tags.parse_tag_list(:P8002_INCLUDING_TAGS2))) connect by prior tag_name = tag_prnt),',
'including_sqls as',
'  (select /* materialize */ sql_id, tag_name',
'     from OPAS_OT_SQL_SQL2TAGS, including_tags',
'    where tag=tag_name),',
'excluding_tags as ',
'  (select tag_name',
'     from OPAS_OT_SQL_TAGS',
'    start with tag_name in (select column_value from table(coreobj_sql_tags.parse_tag_list(:P8002_EXCLUDING_TAGS2))) connect by prior tag_name = tag_prnt),',
'excluding_sqls as',
'  (select /* materialize */ sql_id ',
'     from OPAS_OT_SQL_SQL2TAGS, excluding_tags',
'    where tag=tag_name)',
'select sql_data_point_id1||'':''||sql_data_point_id2 selection_col, ',
'       f1.sql_id1, dt1, from1, descr1, sql_data_point_id1,',
'       f2.sql_id2, dt2, from2, descr2, sql_data_point_id2,',
'       COREOBJ_SQL_SEARCH.get_shorten_sql_text_for_html_expose(f1.file_contentc,:P8002_SQLTEXTLONG) sql_text1,',
